    private void bcalcularActionPerformed(java.awt.event.ActionEvent evt) {
        // Verificar que los campos de texto contengan datos
        if (tvalor1.getText().length() == 0 || tvalor2.getText().length() == 0) {
            JOptionPane.showMessageDialog(this, "Por favor ingrese los dos valores.");
            return;
        }

        // Convertir de string a float
        float valor1 = Float.parseFloat(tvalor1.getText());
        float valor2 = Float.parseFloat(tvalor2.getText());

        float resultado = 0;
        if (rbsuma.isSelected()) {
            resultado = valor1 + valor2;
        } else if (rbresta.isSelected()) {
            resultado = valor1 - valor2;
        } else if (rbmultiplica.isSelected()) {
            resultado = valor1 * valor2;
        } else if (rbdivicion.isSelected()) {
            resultado = valor1 / valor2;
        } else {
            lresultado.setText("No se ha seleccionado ninguna operacion");
            return;
        }

        // Formatear la salida del resultado con dos decimales
        if (resultado % 1 == 0) {
            // El resultado es un n√∫mero entero, mostrarlo sin decimales
            lresultado.setText(String.format("%d", (int) resultado));
        } else {
            // El resultado tiene decimales, mostrarlo con dos decimales
            lresultado.setText(String.format("%.2f", resultado));
        }
    }// GEN-LAST:event_bcalcularActionPerformed

